const initState = [];

const citiesReducer = (state = initState, action) => {
  switch (action.type) {
    case "ADD_CITIES":
      console.log(action.type, action.payload);
      break;
    case "DELETE_CITY":
      let citiesToKeep = state.filter(city => {
        return action._id !== city._id;
      });
      return {
        citiesToKeep
      };
    default:
      return state;
  }
};

export default citiesReducer;
