import React from "react";

function separator(props) {
  //display icon if user is not on the home page
  let style =
    this.props.location.pathname !== "/"
      ? {
          display: "none"
        }
      : {
          height: "70px"
        };
  return (
    <div style={style}>
      <p>dsada</p>
    </div>
  );
}

export default separator;
