function handleErrors(response) {
  if (!response.ok) {
    throw Error(response.statusText);
  }
  return response;
}

export const getCities = () => {
  return (dispatch, getState) => {
    let cities = fetch("/api/cities")
      .then(handleErrors)
      .then(res => res.json());

    dispatch({
      type: "ADD_CITIES",
      payload: cities
    }); /*then continue on with dispatch???*/
  };
};
