const itinerariesReducer = (state = [], action) => {
  switch (action.type) {
    case "FETCH_ITINERARIES":
      return action.payload;
    case "DELETE_ITINERARY":
      let itinsToKeep = state.filter(itinerary => {
        return action._id !== itinerary._id;
      });
      return {
        itinsToKeep
      };
    case "UPDATE_ITINERARY":
      return state.map(itinerary => {
        if (itinerary._id === payload.itineraryID) {
          itinerary = action.payload.updatedItinerary;
        }
        return state;
      });
    default:
      return state;
  }
};

export default itinerariesReducer;
