export const addCurrentItineraryID = {
  type: "ADD_CURRENT_ITINERARY_ID",
  payload: String // itinerary ID
};

// async with thunk

function handleErrors(response) {
  if (!response.ok) {
    throw Error(response.statusText);
  }
  return response;
}

export const getAllItineraries = () => {
  return dispatch => {
    return fetch("/api/itineraries")
      .then(handleErrors)
      .then(res => res.json())
      .then(itineraries => {
        dispatch({
          type: "FETCH_ALL_ITINERARIES",
          payload: itineraries
        });
      });
  };
};

export const getFilteredItineraries = payload => {
  return dispatch => {
    return fetch("/api/itineraries/byCity" + payload)
      .then(handleErrors)
      .then(res => res.json())
      .then(itineraries => {
        dispatch({
          type: "FETCH_FILTERED_ITINERARIES",
          payload: itineraries
        });
      });
  };
};

export const addComment = (itineraryID, comment, timestamp) => {
  let commentToSend = {
    content: comment,
    timestamp: timestamp
  };

  return dispatch => {
    return fetch("/api/itineraries/addComment/" + itineraryID, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(commentToSend) // body data type must match "Content-Type" header
    })
      .then(handleErrors)
      .then(response => response.json()) // parses JSON response into native JavaScript objects
      .then(response => {
        dispatch({
          type: "UPDATE_ITINERARY",
          payload: {
            updatedItinerary: response,
            itineraryID: itineraryID
          }
        });
      });
  };
};
