import React from "react";
import { NavLink, withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { getItineraries } from "../store/actions/itinerariesActions";

//component for showing lists of itineraries. the itinerary details page is a seperate component named ItineraryDetails

class ItinerariesList extends React.Component {
  componentDidMount() {
    this.props.getItineraries(props.match.params.cityName);
  }
  render() {
    return (
      <div>
        <h1>Itineraries List Dummy</h1>
        <h2>{props.match.params.cityName + "'s itineraries:"}</h2>
      </div>
    );
  }
}
//grab cities from redux-store-state and save as params here under this.props.city
const mapStateToProps = state => {
  return {
    itineraries: state.itineraries
  };
};
//store dispatch actions/methods in props here
const mapDispatchToProps = dispatch => {
  return {
    getItineraries: cityName => dispatch(getItineraries(cityName))
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withRouter(ItinerariesList));
