const initState = {
  cities: [
    {
      name: "Barcelona",
      _id: 0,
      preview_img:
        "https://www.discoverwalks.com/blog/wp-content/uploads/2015/09/barcelona-city-big.jpg"
    },
    {
      name: "Amsterdam",
      _id: 1,
      preview_img:
        "https://www.iamexpat.nl/sites/default/files/styles/article--full/public/amsterdam-canal-houses.jpg?itok=rhceR4cw"
    },
    {
      name: "City dummy 1",
      _id: 2,
      preview_img:
        "https://www.discoverwalks.com/blog/wp-content/uploads/2015/09/barcelona-city-big.jpg"
    },
    {
      name: "City dummy 2",
      _id: 3,
      preview_img:
        "https://www.iamexpat.nl/sites/default/files/styles/article--full/public/amsterdam-canal-houses.jpg?itok=rhceR4cw"
    }
  ]
};

const citiesReducer = (state = initState, action) => {
  switch (action.type) {
    case "DELETE_CITY":
      let citiesToKeep = state.filter(city => {
        return action._id !== city._id;
      });
      return {
        ...state,
        cities: citiesToKeep
      };
    default:
      return state;
  }
};

export default citiesReducer;
