import React from "react";
import { NavLink, withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { getCities } from "../store/actions/citiesActions";
import "./css/previewLists.css";

class CitiesList extends React.Component {
  //on mount, fetch cities form MongoDB, set redux state, and store here in props
  componentDidMount() {
    this.props.getCities();
    this.setState({
      citiesFilter: ""
    });
  }

  // determinds relevant cities to filter
  relevantCities() {
    let cities =
      this.state.relevantCities !== ""
        ? this.props.cities.map
        : this.props.cities.map(city => {
            return city.includes(this.state.value) ? city : null;
          });
    this.citiesBody(cities);
  }

  //function that creates styled divs per city
  citiesBody(relevantCities) {
    if (relevantCities !== null) {
      let body = relevantCities(city => {
        return (
          <div className="cardbody" key={city._id}>
            <NavLink to={"/itineraries/" + city.name}>
              <h2>{city.name}</h2>
              <img src={city.preview_img} alt="preview card for city" />
            </NavLink>
          </div>
        );
      });
      return body;
    }
  }
  // function that is called onChange of the searchbar value. updates this.state value
  onChangeOfFilterValue(event) {
    this.setState({
      citiesFilter: event.target.value
    });
  }

  render() {
    return (
      <div>
        <div className="citiesHeading">
          <h1>All Cities</h1>
          <input type="text" onChange={this.onChangeOfFilterValue.bind(this)} />
          <NavLink to="/itineraries/">
            <p>see all itineraries</p>
          </NavLink>
        </div>
        {this.relevantCities()}
      </div>
    );
  }
}
//grab cities from redux-store-state and save as params here under this.props.city
const mapStateToProps = state => {
  return {
    cities: state.cities
  };
};

//store dispatch actions/methods in props here
const mapDispatchToProps = dispatch => {
  return {
    getCities: () => dispatch(getCities())
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withRouter(CitiesList));
