import React from "react";
import { NavLink, withRouter } from "react-router-dom";
import { connect } from "react-redux";

class CitiesList extends React.Component {
  //function that creates styled divs per city
  citiesBody() {
    console.log(this.props.cities);
    let body = this.props.cities.cities.map(city => {
      console.log(city.name);
      // return (
      //   <NavLink to={"/itineraries/" + city.name} key={city._id}>
      //     <h2>{city.name}</h2>
      //     <img src={city.preview_img} alt="preview card for city" />
      //     <button onClick={this.props.deleteCity()}>delete city</button>
      //   </NavLink>
      // );
    });
    return body;
  }
  render() {
    return (
      <div>
        <h1>All Cities</h1>
        {this.citiesBody()}
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    cities: state.cities
  };
};
const mapDispatchToProps = dispatch => {
  return {
    deleteCity: id => {
      dispatch({ type: "DELETE_CITY", _id: id });
    }
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withRouter(CitiesList));
