function handleErrors(response) {
  if (!response.ok) {
    throw Error(response.statusText);
  }
  return response;
}

export const getItineraries = payload => {
  return dispatch => {
    return fetch("/api/itineraries/byCity/" + payload)
      .then(handleErrors)
      .then(res => res.json())
      .then(itineraries => {
        dispatch({
          type: "FETCH_ITINERARIES",
          payload: itineraries
        });
      });
  };
};

// send new comment to API. API will return the updated full itinerary object.
// dispatch to UPDATE_ITINERARY with the returned, updated itinerary object
export const addComment = (itineraryID, comment, timestamp) => {
  let commentToSend = {
    content: comment,
    timestamp: timestamp
  };

  return dispatch => {
    return fetch("/api/itineraries/" + itineraryID, {
      method: "PUT", // *GET, POST, PUT, DELETE, etc.
      headers: {
        "Content-Type": "application/json"
        // 'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: JSON.stringify(commentToSend) // body data type must match "Content-Type" header
    })
      .then(handleErrors)
      .then(response => response.json()) // parses JSON response into native JavaScript objects
      .then(response => {
        dispatch({
          type: "UPDATE_ITINERARY",
          payload: {
            updatedItinerary: response,
            itineraryID: itineraryID
          }
        });
      });
  };
};
