import React from "react";

const Separator = props => {
  //display icon if user is not on the home page
  let styleValue =
    props.location.pathname !== "/"
      ? {
          display: "none"
        }
      : {
          height: "70px"
        };
  return <div style={styleValue}></div>;
};

export default Separator;
